package java_first_project;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("안녕하세요");
		System.out.printf("%d * %d = %d", 10, 2, 10 * 2).println();
		System.out.printf("%.3f", 1.12345).println();
		// 형식 지정자
		// %d: 정수
		// %f: 실수(%.2f: 소수점 숫자 자리까지)
		// %s: 문자열
		// %c: 문자
		// %b: boolean
	}

}
